<?php//------------------- Add section footer// Add Footer section.$wp_customize->add_section(    'footer',    array(        'title'    => __( 'Footer', '' ),        'priority' => 30,    ));// Add color setting.$wp_customize->add_setting(    'footer_color',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',        'section'    => 'footer',        'default'    => '#ffffff',    ));// Add color control.$wp_customize->add_control(    new WP_Customize_Color_Control(        $wp_customize,        'footer_color',        array(            'label'    => __( 'Color first', 'finazi' ),            'section'  => 'footer',            'settings' => 'footer_color',        )    ));// Add background setting.$wp_customize->add_setting(    'footer_background',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',        'section'    => 'footer',        'default'    => '#152639',    ));// Add background control.$wp_customize->add_control(    new WP_Customize_Color_Control(        $wp_customize,        'footer_background',        array(            'label'    => __( 'Background Color first', 'finazi' ),            'section'  => 'footer',            'settings' => 'footer_background',        )    ));// Add color setting.$wp_customize->add_setting(    'footer_end_color',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',        'section'    => 'footer',        'default'    => '#6d8795',    ));// Add color control.$wp_customize->add_control(    new WP_Customize_Color_Control(        $wp_customize,        'footer_end_color',        array(            'label'    => __( 'Color 2nd', 'finazi' ),            'section'  => 'footer',            'settings' => 'footer_end_color',        )    ));// Add background footer end setting.$wp_customize->add_setting(    'footer_end_background',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',        'section'    => 'footer',        'default'    => '#0e1a28',    ));// Add color footer end control.$wp_customize->add_control(    new WP_Customize_Color_Control(        $wp_customize,        'footer_end_background',        array(            'label'    => __( 'Background Color 2nd', 'finazi' ),            'section'  => 'footer',            'settings' => 'footer_end_background',        )    ));// Add fotter left setting.$wp_customize->add_setting(    'footer_left',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',        'section'    => 'footer',        'default'    => 'Copyright Â© 2016 ConsultWP, Designed by FelixDG.',    ));// Add Footer Left control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'footer_left',        array(            'label'    => __( 'Footer Left', 'finazi' ),            'section'  => 'footer',            'settings' => 'footer_left',            'type'     => 'textarea',        )    ));// Add Footer right setting.$wp_customize->add_setting(    'footer_right',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',        'section'    => 'footer',        'default'    => 'FAQs              Sitemap              Community',    ));// Add Footer right control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'footer_right',        array(            'label'    => __( 'Footer right', 'finazi' ),            'section'  => 'footer',            'settings' => 'footer_right',            'type'     => 'textarea',        )    ));//---------------------------------------- Background Image Footer// Add Background Image footer section$wp_customize->add_setting(    'page_footer_background',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',    ));// Add Background Image footer control.$wp_customize->add_control(    new WP_Customize_Image_Control(        $wp_customize,        'page_footer_background',        array(            'label'    => __( 'Background Footer', 'finazi' ),            'section'  => 'footer',            'settings' => 'page_footer_background',        )    ));