<?php//-------------------1 Add panel Header -------------------------// Add panel Header$wp_customize->add_panel(    'header',    array(        'title'      => __( 'Header', 'finazi' ),        'priority'   => 30,    ));//--------------------- Add section ---------------------------------// Add topbar section.$wp_customize->add_section(    'topbar',    array(        'title'    => __( 'Topbar', 'finazi' ),        'panel'    => 'header',    ));// Add section Header Contene.$wp_customize->add_section(    'header_content',    array(        'title'    => __( 'Header Content', 'finazi' ),        'panel'    => 'header',    ));// Add section Page Header hotline.$wp_customize->add_section(    'page_header',    array(        'title'    => __( 'Page header', 'finazi' ),        'panel'    => 'header',    ));//----------------------------------------Setting----------------------------------//--------------------------------------- Page Header Hotline // Add hotline.$wp_customize->add_setting(    'hotline',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',    ));// Add hotline control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'hotline',        array(            'label'    => __( 'Header Content', 'finazi' ),            'section'  => 'header_content',            'settings' => 'hotline',            'type'     => 'textarea',        )    ));// Add background_header_image color.$wp_customize->add_setting(    'background_header_image',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',    ));// Add background color control.$wp_customize->add_control(    new WP_Customize_Color_Control(        $wp_customize,        'background_header_image',        array(            'label'    => __( 'Background Color', 'finazi' ),            'section'  => 'page_header',            'settings' => 'background_header_image',        )    ));//---------------------------------------- Background Image// Add Background Image header section$wp_customize->add_setting(    'page_header_background',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',    ));// Add Background Image header control.$wp_customize->add_control(    new WP_Customize_Image_Control(        $wp_customize,        'page_header_background',        array(            'label'    => __( 'Background', 'finazi' ),            'section'  => 'page_header',            'settings' => 'page_header_background',        )    ));//---------------------------------------- Topbar// Add topbar left setting.$wp_customize->add_setting(    'topbar_left',    array(        'type'              => 'option', // 'option' || 'theme_mod        'capability'        => 'edit_theme_options',        'section'           => 'topbar',    ));// Add topbar left control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'topbar_left',        array(            'label'    => __( 'Header Left', 'finazi' ),            'section'  => 'topbar',            'settings' => 'topbar_left',            'type'     => 'textarea',        )    ));// Add topbar right setting.$wp_customize->add_setting(    'topbar_right',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',    ));// Add topbar right control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'topbar_right',        array(            'label'    => __( 'Header Right', 'finazi' ),            'section'  => 'topbar',            'settings' => 'topbar_right',            'type'     => 'textarea',        )    ));