<?php// Add Shop section.$wp_customize->add_section(    'shop',    array(        'title'    => __( 'Shop', '' ),        'priority' => 30,    ));// Add sidebar page single product.$wp_customize->add_setting(    'shop_sidebar',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',        'section'    => 'shop',        'default'    => 'right',    ));// Add sidebar page single product control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'shop_sidebar',        array(            'label'    => __( 'Sidebar', 'finazi' ),            'section'  => 'shop',            'settings' => 'shop_sidebar',            'type'     => 'select',            'choices'  => array(            'left'     => __( 'Left', 'finazi' ),            'right'    => __( 'Right', 'finazi' ),            'full'     => __( 'No Sidebar', 'finazi' ),            )        )    ));