<?php if ( ! function_exists( 'container_open' ) ) {    function container_open() {        echo '<div class="container">';    }}if ( ! function_exists( 'container_close' ) ) {    function container_close() {        echo '</div>';    }}if ( ! function_exists( 'finazi_button_continue' ) ) {    function finazi_button_continue() {        $shop_page_url = get_permalink( woocommerce_get_page_id( 'shop' ) );        ?>        <a class="button" href="<?php echo esc_url( $shop_page_url ); ?>"><?php esc_html_e( 'Continue Shopping', 'finazi' ); ?></a>        <?php    }}