<?php/** * Elementor Posts Widget * * @package Woostify Pro */namespace Elementor;/** * Class Finazi_Counter_Widget elementor posts widget. */class Finazi_Counter_Widget extends \Elementor\Widget_Base {    /**     * Name     */    public function get_name() {        return 'finazi-counter';    }    /**     * Title     */    public function get_title() {        return esc_html__( 'Counter', 'finazi-pro' );    }    /**     * Icon     */    public function get_icon() {        return 'eicon-counter';    }    /**     * Scripts     */    public function get_script_depends() {        return array( 'finazi-elementor' );    }    /**     * Controls     */    protected function _register_controls() {        $this->start_controls_section(            'post_content',            array(                'label' => esc_html__( 'General', 'finazi-pro' ),            )        );        // Image.        $this->add_control(            'image',            [                'label' => __( 'Choose Image', 'finazi-pro' ),                'type' => \Elementor\Controls_Manager::MEDIA,                'default' => [                    'url' => \Elementor\Utils::get_placeholder_image_src(),                ],            ]        );        // Number.        $this->add_control(            'number',            [                'label'   => __( 'Number', 'finazi-pro' ),                'type'    => \Elementor\Controls_Manager::NUMBER,                'min'     => 0,                'max'     => 1000,                'step'    => 5,                'default' => 10,            ]        );        // Text.        $this->add_control(            'item_description',            [                'label' => __( 'Title', 'finazi-pro' ),                'type' => \Elementor\Controls_Manager::TEXT,                'default' => __( 'Default title', 'finazi-pro' ),                'placeholder' => __( 'Type your title here', 'finazi-pro' ),            ]        );        // Content typography.        $this->add_group_control(            Group_Control_Typography::get_type(),            [                'name' => 'content_typography',                'label' => __( 'Typography', 'finazi-pro' ),                'scheme' => Scheme_Typography::TYPOGRAPHY_1,                'selector' => '{{WRAPPER}} .counter-description',            ]        );        $this->end_controls_section();    }    /**     * Render     */        protected function render() {        $settings = $this->get_settings_for_display();               ?>                        <div class="counter">                <div class="counter-image">                    <?php echo '<img src="' . $settings['image']['url'] . '">'; ?>                </div>                <div class="counter-info">                    <?php echo '<span class="counter_number">' . $settings['number'] . '</span>'; ?>                    <div class="counter-description">                        <?php echo '<p>' . $settings['item_description'] . '</p>'; ?>                    </div>                </div>            </div>        <?php    }}Plugin::instance()->widgets_manager->register_widget_type( new Finazi_Counter_Widget() );