<?php/** * Elementor Posts Widget * * @package Woostify Pro */namespace Elementor;/** * Class Finazi_Intro_Widget elementor posts widget. */class Finazi_Intro_Widget extends \Elementor\Widget_Base {    /**     * Name     */    public function get_name() {        return 'finazi-intro';    }    /**     * Title     */    public function get_title() {        return esc_html__( 'Intro', 'finazi-pro' );    }    /**     * Icon     */    public function get_icon() {        return 'eicon-counter';    }    /**     * Scripts     */    public function get_script_depends() {        return array( 'finazi-elementor' );    }    /**     * Controls     */    protected function _register_controls() {        $this->start_controls_section(            'post_content',            array(                'label' => esc_html__( 'General', 'finazi-pro' ),            )        );        // Image.        $this->add_control(            'image',            [                'label' => __( 'Choose Image', 'finazi-pro' ),                'type' => \Elementor\Controls_Manager::MEDIA,                'default' => [                    'url' => \Elementor\Utils::get_placeholder_image_src(),                ],            ]        );        // Icon.        $this->add_control(            'icon',            [                'label' => __( 'Social Icons', 'finazi-pro' ),                'type' => \Elementor\Controls_Manager::ICON,                'include' => [                    'fa fa-briefcase',                    'fa fa-pie-chart ',                    'fa fa-ship',                    'fa fa-facebook',                    'fa fa-flickr',                    'fa fa-google-plus',                    'fa fa-instagram',                    'fa fa-linkedin',                    'fa fa-pinterest',                    'fa fa-reddit',                    'fa fa-twitch',                    'fa fa-twitter',                    'fa fa-vimeo',                    'fa fa-youtube',                ],                'default' => 'fa fa-briefcase',            ]        );        // Title Heading.        $this->add_control(            'widget_title',            [                'label' => __( 'Title', 'finazi-pro' ),                'type' => \Elementor\Controls_Manager::TEXT,                'default' => __( 'Default title', 'finazi-pro' ),                'placeholder' => __( 'Type your title here', 'finazi-pro' ),            ]        );        // Color title.        $this->add_control(            'title_color',            [                'label' => __( 'Title Color', 'finazi-pro' ),                'type' => \Elementor\Controls_Manager::COLOR,                'scheme' => [                    'type' => \Elementor\Scheme_Color::get_type(),                    'value' => \Elementor\Scheme_Color::COLOR_1,                ],                'selectors' => [                    '{{WRAPPER}} .title' => 'color: {{VALUE}}',                ],            ]        );        // Content typography title heading.        $this->add_group_control(            Group_Control_Typography::get_type(),            [                'name' => 'content_typography',                'label' => __( 'Typography Title Heading', 'finazi-pro' ),                'scheme' => Scheme_Typography::TYPOGRAPHY_1,                'selector' => '{{WRAPPER}} .intro-title',            ]        );        // Textarea.        $this->add_control(            'item_description',            [                'label' => __( 'Description', 'finazi-pro' ),                'type' => \Elementor\Controls_Manager::TEXTAREA,                'rows' => 10,                'default' => __( 'Default description', 'finazi-pro' ),                'placeholder' => __( 'Type your description here', 'finazi-pro' ),            ]        );        // Color intro.        $this->add_control(            'intro_color',            [                'label' => __( 'Intro Color', 'finazi-pro' ),                'type' => \Elementor\Controls_Manager::COLOR,                'scheme' => [                    'type' => \Elementor\Scheme_Color::get_type(),                    'value' => \Elementor\Scheme_Color::COLOR_1,                ],                'selectors' => [                    '{{WRAPPER}} .title' => 'color: {{VALUE}}',                ],            ]        );        // Content typography.        $this->add_group_control(            Group_Control_Typography::get_type(),            [                'name' => 'content_typography_2',                'label' => __( 'Typography', 'finazi-pro' ),                'scheme' => Scheme_Typography::TYPOGRAPHY_1,                'selector' => '{{WRAPPER}} .intro-description',            ]        );        $this->end_controls_section();    }    /**     * Render     */        protected function render() {        $settings = $this->get_settings_for_display();               ?>        <div class="intro">            <div class="image-intro">                <?php echo '<img src="' . $settings['image']['url'] . '">'; ?>            </div>            <div class="info-intro">                <div class="intro-title">                    <?php echo '<i class="' . $settings['icon'] . '" ></i>                    <h2 class="title" style="color: ' . $settings['title_color'] . '">' . $settings['widget_title'] . '</h2>'; ?>                </div>                <div class="intro-description">                    <?php echo '<p style="color: ' . $settings['intro_color'] . '">' . $settings['item_description'] . '</p>'; ?>                </div>            </div>        </div>        <?php    }}Plugin::instance()->widgets_manager->register_widget_type( new Finazi_Intro_Widget() );