<?php/** * Elementor Posts Widget * * @package Woostify Pro */namespace Elementor;/** * Class Finazi_Service_Widget woostify elementor posts widget. */class Finazi_Service_Widget extends \Elementor\Widget_Base {    /**     * Name     */    public function get_name() {        return 'finazi-service';    }    /**     * Title     */    public function get_title() {        return esc_html__( 'Service', 'finazi-pro' );    }    /**     * Icon     */    public function get_icon() {        return 'eicon-post';    }    // Controls widget.    protected function _register_controls() {        $this->start_controls_section(            'post_content',            array(                'label' => esc_html__( 'General', 'finazi-pro' ),            )        );        // Layout.        $this->add_control(            'layout',            array(                'type'    => Controls_Manager::SELECT,                'label'   => esc_html__( 'Layout', 'finazi-pro' ),                'default' => 'grid',                'options' => array(                    'grid'     => __( 'Grid', 'finazi-pro' ),                    'carousel' => __( 'Carousel', 'finazi-pro' ),                ),            )        );        $this->end_controls_section();    }    // Printer Widget Preview.    protected function _content_template() {        ?>        <div style="border-style: {{ settings.border_style }}"> .. </div>        <?php    }    // Printer Widget.    protected function render() {        $settings = $this->get_settings_for_display();        echo '<div style="border-style: ' . $settings['border_style'] . '"> .. </div>';    }}Plugin::instance()->widgets_manager->register_widget_type( new Finazi_Service_Widget() );