<?php/** * Elementor Posts Widget * * @package Woostify Pro */namespace Elementor;/** * Class Finazi_Testimonial_Widget elementor posts widget. */class Finazi_Testimonial_Widget extends \Elementor\Widget_Base {    /**     * Name     */    public function get_name() {        return 'finazi-testimonial';    }    /**     * Title     */    public function get_title() {        return esc_html__( 'Testimonial Finazi', 'finazi-pro' );    }    /**     * Icon     */    public function get_icon() {        return 'eicon-testimonial';    }    /**     * Scripts     */    public function get_script_depends() {        return array( 'finazi-elementor' );    }    protected function _register_controls() {        $this->start_controls_section(            'content_section',            [                'label' => __( 'Content', 'plugin-name' ),                'tab' => \Elementor\Controls_Manager::TAB_CONTENT,            ]        );        $repeater = new \Elementor\Repeater();        // Title Heading.        $repeater->add_control(            'list_title',            [                'label' => __( 'Title', 'finazi-pro' ),                'type' => \Elementor\Controls_Manager::TEXT,                'default' => __( 'Default title', 'finazi-pro' ),                'placeholder' => __( 'Type your title here', 'finazi-pro' ),            ]        );        // Image.        $repeater->add_control(            'list_content',            [                'label' => __( 'Choose Image', 'finazi-pro' ),                'type' => \Elementor\Controls_Manager::MEDIA,                'default' => [                    'url' => \Elementor\Utils::get_placeholder_image_src(),                ],            ]        );        $repeater->add_control(            'list_color',            [                'label' => __( 'Color', 'plugin-domain' ),                'type' => \Elementor\Controls_Manager::COLOR,                'selectors' => [                    '{{WRAPPER}} {{CURRENT_ITEM}}' => 'color: {{VALUE}}'                ],            ]        );        $this->add_control(            'list',            [                'label' => __( 'Repeater List', 'plugin-domain' ),                'type' => \Elementor\Controls_Manager::REPEATER,                'fields' => $repeater->get_controls(),                'default' => [                    [                        'list_title' => __( 'Title #1', 'plugin-domain' ),                        'list_content' => __( 'Item content. Click the edit button to change this text.', 'plugin-domain' ),                    ],                    [                        'list_title' => __( 'Title #2', 'plugin-domain' ),                        'list_content' => __( 'Item content. Click the edit button to change this text.', 'plugin-domain' ),                    ],                ],                'title_field' => '{{{ list_title }}}',            ]        );        $this->end_controls_section();    }    /**     * Controls     */    /**     * Render     */        protected function render() {        $settings = $this->get_settings();        $this->add_render_attribute( 'wrapper', 'class', 'elementor-testimonial-wrapper' );        if ( $settings['list'] ) {            echo '<dl>';            foreach (  $settings['list'] as $item ) {                 echo '<dt class="elementor-repeater-item-' . $item['_id'] . '">' . $item['list_title'] . '</dt>';                echo '<img src="' . $item['list_content']['url'] . '">';            }            echo '</dl>';        }    }}Plugin::instance()->widgets_manager->register_widget_type( new Finazi_Testimonial_Widget() );